import { View, Text } from 'react-native';
import { StyleSheet } from 'react-native';

const style = StyleSheet.create({
  text: {
    flex: 1,
    //alignItems: 'center',
    justifyContent: 'space-between',
    flexDirection: 'row',
    padding: 5,
  }
});

const ConnectText = ({ connected, uptime }) => {
  if (!connected) {
    return (
      <Text style={style.text}>Connecting...</Text>
    );
  }
  return (
    <View style={style.text}>
      <Text>Conntected.</Text>
      <Text>Up {uptime.hours}:{uptime.minutes}:{uptime.seconds}</Text>
    </View>
  );
}

export default ConnectText;
