import { View, Text } from 'react-native';
import { StyleSheet } from 'react-native';


const styles = StyleSheet.create({
  cards: {
    flex: 1,
    flexDirection: 'row'
  },
  card: {
    backgroundColor: 'red',
    height: 48,
  },
  text: {
    fontSize: 14,
    letterSpacing: 1
  }
});

const Cards = ({ temperature, humidity }) =>
  <View style={styles.cards}>
    <TempCard /><HumidCard />
  </View>;


const TempCard = ({ temperature }) =>
  <View style={styles.card}><Text style={styles.text}>34.3*C</Text></View>;

const HumidCard = ({ humidity }) =>
  <View style={styles.card}><Text style={styles.text}>64%</Text></View>;


export default Cards;
